import{o as t,c as e,a as l}from"./app.1f338b60.js";const s='{"title":"Typography","description":"","frontmatter":{},"headers":[{"level":2,"title":"Mixin","slug":"mixin"},{"level":3,"title":"Titles/Headings/Headlines","slug":"titles-headings-headlines"},{"level":3,"title":"Body","slug":"body"},{"level":3,"title":"Utils","slug":"utils"},{"level":3,"title":"Buttons","slug":"buttons"}],"relativePath":"textual/mixin.md","lastUpdated":1624965006411}',n={},a=l('<h1 id="typography"><a class="header-anchor" href="#typography" aria-hidden="true">#</a> Typography</h1><h2 id="mixin"><a class="header-anchor" href="#mixin" aria-hidden="true">#</a> Mixin</h2><p>All typography coming from the Crowny-UI can be used styled via the typography mixin: <br> You can call the SCSS mixin on any element to give it it&#39;s textual styling.</p><div class="language-scss"><pre><code><span class="token keyword">@include</span> mixins.<span class="token function">typography</span><span class="token punctuation">(</span><span class="token string">&#39;body&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;base&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n\n<span class="token comment">/*\nfont-family: &#39;Inter&#39;, sans-serif;\nfont-weight: 400;\nline-height: 1.5;\nfont-size: 1rem; (16px)\n*/</span>\n</code></pre></div><div class="danger custom-block"><p class="custom-block-title">Important</p><p><code>mixins.</code> namespacing is required.</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>When calling the <code>@include mixins.typography()</code> we use 2 parameters.</p><ol><li>Key (Name of the array)</li><li>Entry (Name of the option)</li></ol></div><h3 id="titles-headings-headlines"><a class="header-anchor" href="#titles-headings-headlines" aria-hidden="true">#</a> Titles/Headings/Headlines</h3><p>Within the typography mixin we have an array of headlines.</p><p><strong>Example</strong></p><div class="language-scss"><pre><code><span class="token keyword">@include</span> mixins.<span class="token function">typography</span><span class="token punctuation">(</span><span class="token string">&#39;headlines&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;h1&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><table><thead><tr><th style="text-align:left;">Key</th><th style="text-align:left;">Entry</th><th style="text-align:left;">Font size</th></tr></thead><tbody><tr><td style="text-align:left;">headlines</td><td style="text-align:left;">h1</td><td style="text-align:left;">24px - 64px</td></tr><tr><td style="text-align:left;">headlines</td><td style="text-align:left;">h2</td><td style="text-align:left;">24px - 30px</td></tr><tr><td style="text-align:left;">headlines</td><td style="text-align:left;">h3</td><td style="text-align:left;">20px - 30px</td></tr><tr><td style="text-align:left;">headlines</td><td style="text-align:left;">h4</td><td style="text-align:left;">24px - 30px</td></tr><tr><td style="text-align:left;">headlines</td><td style="text-align:left;">h5</td><td style="text-align:left;">24px - 30px</td></tr></tbody></table><h3 id="body"><a class="header-anchor" href="#body" aria-hidden="true">#</a> Body</h3><p>Within the typography mixin we have an array of body styles. Body always uses the font <code>Inter</code></p><p><strong>Example</strong></p><div class="language-scss"><pre><code><span class="token keyword">@include</span> mixins.<span class="token function">typography</span><span class="token punctuation">(</span><span class="token string">&#39;body&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;xl&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><table><thead><tr><th style="text-align:left;">Key</th><th style="text-align:left;">Entry</th><th style="text-align:left;">Font size</th><th style="text-align:left;">Font weight</th></tr></thead><tbody><tr><td style="text-align:left;">body</td><td style="text-align:left;">xs</td><td style="text-align:left;">12px</td><td style="text-align:left;">400</td></tr><tr><td style="text-align:left;">body</td><td style="text-align:left;">sm</td><td style="text-align:left;">14px</td><td style="text-align:left;">400</td></tr><tr><td style="text-align:left;">body</td><td style="text-align:left;">base</td><td style="text-align:left;">16px</td><td style="text-align:left;">400</td></tr><tr><td style="text-align:left;">body</td><td style="text-align:left;">lg</td><td style="text-align:left;">18px</td><td style="text-align:left;">400</td></tr><tr><td style="text-align:left;">body</td><td style="text-align:left;">xl</td><td style="text-align:left;">20px</td><td style="text-align:left;">400</td></tr></tbody></table><h3 id="utils"><a class="header-anchor" href="#utils" aria-hidden="true">#</a> Utils</h3><p>Within the typography mixin we have an array of utility styles. Utils always uses the font <code>Inter</code></p><p><strong>Example</strong></p><div class="language-scss"><pre><code><span class="token keyword">@include</span> mixins.<span class="token function">typography</span><span class="token punctuation">(</span><span class="token string">&#39;utils&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;caption&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><table><thead><tr><th style="text-align:left;">Key</th><th style="text-align:left;">Entry</th><th style="text-align:left;">Font size</th><th style="text-align:left;">Font weight</th><th style="text-align:left;">Uppercase</th></tr></thead><tbody><tr><td style="text-align:left;">utils</td><td style="text-align:left;">link</td><td style="text-align:left;">16px</td><td style="text-align:left;">500</td><td style="text-align:left;">No</td></tr><tr><td style="text-align:left;">utils</td><td style="text-align:left;">caption</td><td style="text-align:left;">16px</td><td style="text-align:left;">600</td><td style="text-align:left;">No</td></tr><tr><td style="text-align:left;">utils</td><td style="text-align:left;">breadcrumbs</td><td style="text-align:left;">14px</td><td style="text-align:left;">500</td><td style="text-align:left;">No</td></tr><tr><td style="text-align:left;">utils</td><td style="text-align:left;">label</td><td style="text-align:left;">12px</td><td style="text-align:left;">500</td><td style="text-align:left;">Yes</td></tr><tr><td style="text-align:left;">utils</td><td style="text-align:left;">micro</td><td style="text-align:left;">9px</td><td style="text-align:left;">500</td><td style="text-align:left;">No</td></tr></tbody></table><h3 id="buttons"><a class="header-anchor" href="#buttons" aria-hidden="true">#</a> Buttons</h3><p>Within the typography mixin we have an array of button styles. Buttons always uses the font <code>Inter</code></p><p><strong>Example</strong></p><div class="language-scss"><pre><code><span class="token keyword">@include</span> mixins.<span class="token function">typography</span><span class="token punctuation">(</span><span class="token string">&#39;buttons&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;xs-small&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><table><thead><tr><th style="text-align:left;">Key</th><th style="text-align:left;">Entry</th><th style="text-align:left;">Font size</th><th style="text-align:left;">Font weight</th><th style="text-align:left;">Uppercase</th></tr></thead><tbody><tr><td style="text-align:left;">buttons</td><td style="text-align:left;">xs-small</td><td style="text-align:left;">12px</td><td style="text-align:left;">500</td><td style="text-align:left;">No</td></tr><tr><td style="text-align:left;">buttons</td><td style="text-align:left;">medium-large</td><td style="text-align:left;">14px</td><td style="text-align:left;">500</td><td style="text-align:left;">No</td></tr><tr><td style="text-align:left;">buttons</td><td style="text-align:left;">xl-xxl</td><td style="text-align:left;">16px</td><td style="text-align:left;">500</td><td style="text-align:left;">No</td></tr></tbody></table>',26);n.render=function(l,s,n,i,d,p){return t(),e("div",null,[a])};export default n;export{s as __pageData};
