import{o as a,c as s,a as n}from"./app.d56d8a56.js";const i='{"title":"Crowny gradients","description":"","frontmatter":{},"headers":[{"level":2,"title":"Gradients","slug":"gradients"},{"level":3,"title":"Primary","slug":"primary"},{"level":3,"title":"Secondary","slug":"secondary"},{"level":3,"title":"Tertiary","slug":"tertiary"},{"level":3,"title":"Accents","slug":"accents"},{"level":3,"title":"Light backgrounds","slug":"light-backgrounds"},{"level":3,"title":"Dark backgrounds","slug":"dark-backgrounds"}],"relativePath":"utils/gradients.md","lastUpdated":1624627925119}',e={},t=n('<h1 id="crowny-gradients"><a class="header-anchor" href="#crowny-gradients" aria-hidden="true">#</a> Crowny gradients</h1><h2 id="gradients"><a class="header-anchor" href="#gradients" aria-hidden="true">#</a> Gradients</h2><p>All gradients coming from the Crowny-UI can be used in two different ways: <br> You can apply a class <em>(specified per gradient below)</em> to any element that needs a gradient background e.g</p><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>gradient-primary-1<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><p>Or use the SCSS mixin instead <em>(and use the gradient name specified below)</em></p><div class="language-scss"><pre><code><span class="token keyword">@include</span> mixins.<span class="token function">gradient</span><span class="token punctuation">(</span><span class="token string">&#39;primary-1&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;linear&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n<span class="token keyword">@include</span> mixins.<span class="token function">gradient</span><span class="token punctuation">(</span><span class="token string">&#39;primary-1&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;radial&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><div class="danger custom-block"><p class="custom-block-title">Important</p><p><code>mixins.</code> namespacing is required.</p></div><div class="warning custom-block"><p class="custom-block-title">Important</p><p>Currently we have <strong>no</strong> radial gradients defined in the Crowny-UI. <br> The example above is <strong>purely an example</strong> of the gradient mixin.</p></div><h3 id="primary"><a class="header-anchor" href="#primary" aria-hidden="true">#</a> Primary</h3><div class="color-grid"><div class="card"><div class="gradient-primary-1"></div><cite>SCSS mixin name</cite><span>primary-1</span><cite>Class</cite><span>gradient-primary-1</span></div><div class="card"><div class="gradient-primary-2"></div><cite>SCSS mixin name</cite><span>primary-2</span><cite>Class</cite><span>gradient-primary-2</span></div></div>   <h3 id="secondary"><a class="header-anchor" href="#secondary" aria-hidden="true">#</a> Secondary</h3><div class="color-grid"><div class="card"><div class="gradient-secondary-1"></div><cite>SCSS mixin name</cite><span>secondary-1</span><cite>Class</cite><span>gradient-secondary-1</span></div><div class="card"><div class="gradient-secondary-2"></div><cite>SCSS mixin name</cite><span>secondary-2</span><cite>Class</cite><span>gradient-secondary-2</span></div></div>   <h3 id="tertiary"><a class="header-anchor" href="#tertiary" aria-hidden="true">#</a> Tertiary</h3><div class="color-grid"><div class="card"><div class="gradient-tertiary-1"></div><cite>SCSS mixin name</cite><span>tertiary-1</span><cite>Class</cite><span>gradient-tertiary-1</span></div><div class="card"><div class="gradient-tertiary-2"></div><cite>SCSS mixin name</cite><span>tertiary-2</span><cite>Class</cite><span>gradient-tertiary-2</span></div></div>   <h3 id="accents"><a class="header-anchor" href="#accents" aria-hidden="true">#</a> Accents</h3><div class="color-grid"><div class="card"><div class="gradient-strangerdanger"></div><cite>SCSS mixin name</cite><span>strangerdanger</span><cite>Class</cite><span>gradient-strangerdanger</span></div><div class="card"><div class="gradient-uponly"></div><cite>SCSS mixin name</cite><span>uponly</span><cite>Class</cite><span>gradient-uponly</span></div><div class="card"><div class="gradient-sunrise"></div><cite>SCSS mixin name</cite><span>sunrise</span><cite>Class</cite><span>gradient-sunrise</span></div><div class="card"><div class="gradient-edibleantifreeze"></div><cite>SCSS mixin name</cite><span>edibleantifreeze</span><cite>Class</cite><span>gradient-edibleantifreeze</span></div></div>   <h3 id="light-backgrounds"><a class="header-anchor" href="#light-backgrounds" aria-hidden="true">#</a> Light backgrounds</h3><div class="color-grid"><div class="card"><div class="gradient-light-background"></div><cite>SCSS mixin name</cite><span>light-background</span><cite>Class</cite><span>gradient-light-background</span></div><div class="card"><div class="gradient-light-background-2"></div><cite>SCSS mixin name</cite><span>light-background-2</span><cite>Class</cite><span>gradient-light-background-2</span></div></div>   <h3 id="dark-backgrounds"><a class="header-anchor" href="#dark-backgrounds" aria-hidden="true">#</a> Dark backgrounds</h3><div class="color-grid"><div class="card"><div class="gradient-dark-background"></div><cite>SCSS mixin name</cite><span>dark-background</span><cite>Class</cite><span>gradient-dark-background</span></div><div class="card"><div class="gradient-dark-background-2"></div><cite>SCSS mixin name</cite><span>dark-background-2</span><cite>Class</cite><span>gradient-dark-background-2</span></div></div>   ',26);e.render=function(n,i,e,r,d,c){return a(),s("div",null,[t])};export default e;export{i as __pageData};
